 53	
 54	
 55	FGameplayAttribute::FGameplayAttribute(FProperty *NewProperty)
 56	{
 57		// we allow numeric properties and gameplay attribute data properties for now
 58		// @todo deprecate numeric properties
 59		Attribute = CastField<FNumericProperty>(NewProperty);
 60		AttributeOwner = nullptr;
 61	
 62		if (!Attribute.Get())
 63		{
 64			if (IsGameplayAttributeDataProperty(NewProperty))
 65			{
 66				Attribute = NewProperty;
 67			}
 68		}
 69	
 70		if (Attribute.Get())
 71		{
 72	 		AttributeOwner = Attribute->GetOwnerStruct();
 73	 		Attribute->GetName(AttributeName);
 74		}
 75	}
