 313			if (Class->IsChildOf(UAttributeSet::StaticClass()) 
 314	#if WITH_EDITORONLY_DATA
 315				// ClassGeneratedBy TODO: This is wrong in cooked builds
 316				&& !Class->ClassGeneratedBy
 317	#endif
 318				)
