 325					{
 326						continue;
 327					}
 328					#endif
 329				}
 330	
 331				if (Class == UAbilitySystemTestAttributeSet::StaticClass())
 332				{
 333					continue;
 334				}
...
 443	
 444		for( TFieldIterator<FProperty> It(GetClass(), EFieldIteratorFlags::IncludeSuper) ; It ; ++It )
 445		{
 446			FProperty* Property = *It;
 447			FNumericProperty *NumericProperty = CastField<FNumericProperty>(Property);
 448			if (NumericProperty)
 449			{
 450				FString RowNameStr = FString::Printf(TEXT("%s.%s"), *Property->GetOwnerVariant().GetName(), *Property->GetName());
 451			
 452				FAttributeMetaData * MetaData = DataTable->FindRow<FAttributeMetaData>(FName(*RowNameStr), Context, false);
 453				if (MetaData)
 454				{
 455					void *Data = NumericProperty->ContainerPtrToValuePtr<void>(this);
 456					NumericProperty->SetFloatingPointPropertyValue(Data, MetaData->BaseValue);
 457				}
 458			}
 459			else if (FGameplayAttribute::IsGameplayAttributeDataProperty(Property))
 460			{
 461				FString RowNameStr = FString::Printf(TEXT("%s.%s"), *Property->GetOwnerVariant().GetName(), *Property->GetName());
 462	
 463				FAttributeMetaData * MetaData = DataTable->FindRow<FAttributeMetaData>(FName(*RowNameStr), Context, false);
 464				if (MetaData)
 465				{
 466					FStructProperty* StructProperty = CastField<FStructProperty>(Property);
 467					check(StructProperty);
 468					FGameplayAttributeData* DataPtr = StructProperty->ContainerPtrToValuePtr<FGameplayAttributeData>(this);
 469					check(DataPtr);
 470					DataPtr->SetBaseValue(MetaData->BaseValue);
 471					DataPtr->SetCurrentValue(MetaData->BaseValue);
 472				}
 473			}
 474		}
 475	
 476		PrintDebug();
