...
 167	
 168	float UAbilitySystemComponent::GetGameplayAttributeValue(FGameplayAttribute Attribute, OUT bool& bFound) const
 169	{
 170		// validate the attribute
 171		if (Attribute.IsValid())
 172		{
 173			// get the associated AttributeSet
 174			const UAttributeSet* InternalAttributeSet = GetAttributeSubobject(Attribute.GetAttributeSetClass());
 175	
 176			if (InternalAttributeSet)
 177			{
 178				// NOTE: this is currently not taking predicted gameplay effect modifiers into consideration, so the value may not be accurate on client
 179				bFound = true;
 180				return Attribute.GetNumericValue(InternalAttributeSet);
 181			}
 182		}
 183	
 184		// the attribute was not found
 185		bFound = false;
 186		return 0.0f;
 187	}
...
 370	
 371	void UAbilitySystemComponent::CaptureAttributeForGameplayEffect(OUT FGameplayEffectAttributeCaptureSpec& OutCaptureSpec)
 372	{
 373		// Verify the capture is happening on an attribute the component actually has a set for; if not, can't capture the value
 374		const FGameplayAttribute& AttributeToCapture = OutCaptureSpec.BackingDefinition.AttributeToCapture;
 375		if (AttributeToCapture.IsValid() && (AttributeToCapture.IsSystemAttribute() || GetAttributeSubobject(AttributeToCapture.GetAttributeSetClass())))
 376		{
 377			ActiveGameplayEffects.CaptureAttributeForGameplayEffect(OutCaptureSpec);
 378		}
 379	}
