 992	template<typename FieldType>
 993	FUNCTION_NON_NULL_RETURN_START
 994	FORCEINLINE FieldType* CastFieldChecked(FField* Src)
 995	FUNCTION_NON_NULL_RETURN_END
 996	{
 997	#if !DO_CHECK
 998		return (FieldType*)Src;
 999	#else
 1000		FieldType* CastResult = Src && Src->IsA<FieldType>() ? static_cast<FieldType*>(Src) : nullptr;
 1001		checkf(CastResult, TEXT("CastFieldChecked failed with 0x%016llx"), (int64)(PTRINT)Src);
 1002		return CastResult;
 1003	#endif // !DO_CHECK
 1004	}
 1005	
 1006	template<typename FieldType>
 1007	FUNCTION_NON_NULL_RETURN_START
 1008	FORCEINLINE const FieldType* CastFieldChecked(const FField* Src)
 1009	FUNCTION_NON_NULL_RETURN_END
 1010	{
 1011	#if !DO_CHECK
 1012		return (const FieldType*)Src;
 1013	#else
 1014		const FieldType* CastResult = Src && Src->IsA<FieldType>() ? static_cast<const FieldType*>(Src) : nullptr;
 1015		checkf(CastResult, TEXT("CastFieldChecked failed with 0x%016llx"), (int64)(PTRINT)Src);
 1016		return CastResult;
 1017	#endif // !DO_CHECK
 1018	}
