 6829	T* FindUField(const UStruct* Owner, FName FieldName, EFieldIterationFlags IterationFlags = EFieldIterationFlags::Default)
 6830	{
 6831		static_assert(sizeof(T) > 0, "T must not be an incomplete type");
 6832	
