 6961	/**
 6962	 * Search for the named field within the specified scope, including any Outer classes; assert on failure.
 6963	 *
 6964	 * @param	Scope		the scope to search for the field in
 6965	 * @param	FieldName	the name of the field to search for.
 6966	 */
 6967	template<typename T>
 6968	T* FindFieldChecked( const UStruct* Scope, FName FieldName )
 6969	{
 6970		if ( FieldName != NAME_None && Scope != NULL )
 6971		{
 6972			const UStruct* InitialScope = Scope;
 6973			for ( ; Scope != NULL; Scope = dynamic_cast<const UStruct*>(Scope->GetOuter()) )
 6974			{
 6975				for ( TFieldIterator<T> It(Scope); It; ++It )
 6976				{
 6977					if ( It->GetFName() == FieldName )
 6978					{
 6979						return *It;
 6980					}
 6981				}
 6982			}
 6983		
 6984			UE_LOG( LogType, Fatal, TEXT("Failed to find %s %s in %s"), *T::StaticClass()->GetName(), *FieldName.ToString(), *InitialScope->GetFullName() );
 6985		}
 6986	
 6987		return NULL;
