 577		for (TFieldIterator<const FProperty> PropIt(InStruct, EFieldIterationFlags::IncludeSuper); PropIt; ++PropIt)
 578		{
 579			const FString PropertyNameStr = InStruct->GetAuthoredNameForField(*PropIt);
 580			const FName PropertyName = *PropertyNameStr;
 581			if (NamesToLookFor.Contains(PropertyName)
 582				|| (!PropertyName.IsValidXName() && NamesToLookFor.Contains(MakeObjectNameFromDisplayLabel(PropertyNameStr, NAME_None))))
 583			{
