 290	template<>
 291	inline UFunction* ResolveUFunctionImpl(FName MemberName)
 292	{
 293		UFunction* ReturnField = nullptr;
 294		FString const StringName = MemberName.ToString();
 295		for (TObjectIterator<UPackage> PackageIt; PackageIt && (ReturnField == nullptr); ++PackageIt)
 296		{
 297			if (PackageIt->HasAnyPackageFlags(PKG_CompiledIn) == false)
 298			{
 299				continue;
 300			}
 301	
 302			// NOTE: this could return the wrong field (if there are 
 303			// two like-named delegates defined in separate packages)
 304			ReturnField = FindObject<UFunction>(*PackageIt, *StringName);
 305		}
 306		return ReturnField;
 307	}
