 38	class TAllFieldsIterator
 39	{
 40	private:
 41		/** Iterator for iterating over all UStructs */
 42		TObjectIterator<UStruct> StructIterator;
 43		/** 
 44		 * Iterator for iterating over all child UFields or FFields 
 45		 * Note that we're going to be iterating over all fields (BaseFieldClass is either FField or UField), not just the ones that match the template argument because we also want
 46		 * to be able to iterate inner fields (for example FArrayProperty::Inner, FSetProperty::ElementProp etc)
 47		 */
 48		TFieldIterator<typename T::BaseFieldClass> FieldIterator;
...
 150	
 151		/** Initializes the field iterator for the current struct */
 152		inline void InitFieldIterator()
 153		{
 154			while (StructIterator)
 155			{
 156				FieldIterator.~TFieldIterator<typename T::BaseFieldClass>();
 157				new (&FieldIterator) TFieldIterator<typename T::BaseFieldClass>(*StructIterator, EFieldIteratorFlags::ExcludeSuper, EFieldIteratorFlags::IncludeDeprecated, EFieldIteratorFlags::IncludeInterfaces);
 158				if (!FieldIterator)
 159				{
 160					// This struct has no fields, check the next one
 161					++StructIterator;
 162					CurrentFieldIndex = -1;
 163				}
 164				else
 165				{
 166					InitCurrentFields();
 167					IterateToNextField();
 168	
 169					if (!FieldIterator)
 170					{
 171						// If the field iterator is invalid after IterateToNextField() call then no fields of the speficied template type were found
 172						++StructIterator;
 173					}
 174					else
 175					{
 176						break;
 177					}
 178				}
 179			}
 180		}
