 11	/**
 12	 * This handle is required for things outside of FActiveGameplayEffectsContainer to refer to a specific active GameplayEffect
 13	 *	For example if a skill needs to create an active effect and then destroy that specific effect that it created, it has to do so
 14	 *	through a handle. a pointer or index into the active list is not sufficient. These are not synchronized between clients and server.
 15	 */
 16	USTRUCT(BlueprintType)
 17	struct FActiveGameplayEffectHandle
 18	{
 19		GENERATED_USTRUCT_BODY()
 20	
 21		FActiveGameplayEffectHandle()
 22			: Handle(INDEX_NONE),
 23			bPassedFiltersAndWasExecuted(false)
 24		{
 25		}
 26	
 27		FActiveGameplayEffectHandle(int32 InHandle)
 28			: Handle(InHandle),
 29			bPassedFiltersAndWasExecuted(true)
 30		{
 31		}
 32	
 33		/** True if this is tracking an active ongoing gameplay effect */
 34		bool IsValid() const
 35		{
 36			return Handle != INDEX_NONE;
 37		}
 38	
 39		/** True if this applied a gameplay effect. This can be true when it is not valid if it applied an instant effect */
 40		bool WasSuccessfullyApplied() const
 41		{
