 236	
 237	bool FGameplayEffectContext::NetSerialize(FArchive& Ar, class UPackageMap* Map, bool& bOutSuccess)
 238	{
 239		//!!!!!!!!!!!!!!!! Warning !!!!!!!!!!!!!!!
 240		// Any changes to this function also need to be done to FGameplayEffectContextNetSerializer to support Iris replication
 241		//!!!!!!!!!!!!!!!! Warning !!!!!!!!!!!!!!!
 242	
 243		uint8 RepBits = 0;
 244		if (Ar.IsSaving())
 245		{
 246			if (bReplicateInstigator && Instigator.IsValid())
 247			{
 248				RepBits |= 1 << 0;
 249			}
 250			if (bReplicateEffectCauser && EffectCauser.IsValid() )
 251			{
 252				RepBits |= 1 << 1;
 253			}
 254			if (AbilityCDO.IsValid())
 255			{
 256				RepBits |= 1 << 2;
 257			}
 258			if (bReplicateSourceObject && SourceObject.IsValid())
 259			{
 260				RepBits |= 1 << 3;
 261			}
 262			if (Actors.Num() > 0)
 263			{
 264				RepBits |= 1 << 4;
 265			}
 266			if (HitResult.IsValid())
 267			{
 268				RepBits |= 1 << 5;
 269			}
 270			if (bHasWorldOrigin)
 271			{
 272				RepBits |= 1 << 6;
 273			}
 274		}
 275	
 276		Ar.SerializeBits(&RepBits, 7);
 277	
 278		if (RepBits & (1 << 0))
 279		{
 280			Ar << Instigator;
 281		}
 282		if (RepBits & (1 << 1))
 283		{
 284			Ar << EffectCauser;
 285		}
 286		if (RepBits & (1 << 2))
 287		{
 288			Ar << AbilityCDO;
 289		}
 290		if (RepBits & (1 << 3))
 291		{
 292			Ar << SourceObject;
 293		}
 294		if (RepBits & (1 << 4))
 295		{
 296			SafeNetSerializeTArray_Default<31>(Ar, Actors);
 297		}
 298		if (RepBits & (1 << 5))
 299		{
 300			if (Ar.IsLoading())
 301			{
 302				if (!HitResult.IsValid())
 303				{
 304					HitResult = TSharedPtr<FHitResult>(new FHitResult());
 305				}
 306			}
 307			HitResult->NetSerialize(Ar, Map, bOutSuccess);
 308		}
 309		if (RepBits & (1 << 6))
 310		{
 311			Ar << WorldOrigin;
 312			bHasWorldOrigin = true;
 313		}
 314		else
 315		{
 316			bHasWorldOrigin = false;
 317		}
 318	
 319		if (Ar.IsLoading())
 320		{
 321			AddInstigator(Instigator.Get(), EffectCauser.Get()); // Just to initialize InstigatorAbilitySystemComponent
 322		}	
 323		
 324		bOutSuccess = true;
 325		return true;
 326	}
