 22	
 23	void AAuraEffectActor::ApplyEffectToTarget(AActor* TargetActor, TSubclassOf<UGameplayEffect> GameplayEffectClass)
 24	{
 25		// 获取目标GAS组件
 26		UAbilitySystemComponent* TargetAsc = UAbilitySystemBlueprintLibrary::GetAbilitySystemComponent(TargetActor);
 27		if (TargetAsc == nullptr)
 28			return;
 29		check(GameplayEffectClass);
 30		FGameplayEffectContextHandle EffectContextHandle = TargetAsc->MakeEffectContext();
 31		EffectContextHandle.AddSourceObject(this);
 32		const FGameplayEffectSpecHandle EffectSpecHandle = TargetAsc->MakeOutgoingSpec(
 33			GameplayEffectClass, 1.f, EffectContextHandle);
 34		// 解指针引用
 35		const FActiveGameplayEffectHandle ActiveEffectHandle = TargetAsc->ApplyGameplayEffectSpecToSelf(
 36			*EffectSpecHandle.Data.Get());
