 6824	
 6825	template <
 6826		typename T
 6827		UE_REQUIRES(std::is_base_of_v<UField, T>)
 6828	>
 6829	T* FindUField(const UStruct* Owner, FName FieldName, EFieldIterationFlags IterationFlags = EFieldIterationFlags::Default)
 6830	{
 6831		static_assert(sizeof(T) > 0, "T must not be an incomplete type");
 6832	
 6833		// We know that a "none" field won't exist in this Struct
 6834		if (FieldName.IsNone())
 6835		{
 6836			return nullptr;
 6837		}
 6838	
 6839		// Search by comparing FNames (INTs), not strings
 6840		for (TFieldIterator<T>It(Owner, IterationFlags); It; ++It)
 6841		{
 6842			if (It->GetFName() == FieldName)
 6843			{
 6844				return *It;
 6845			}
 6846		}
 6847	
 6848		// If we didn't find it, return no field
 6849		return nullptr;
 6850	}
