 14	
 15	namespace UE::AvaCore
 16	{
 17		/**
 18		 * Find Property Conditions:
 19		 * 1) InStructType has a way to get UStruct (either via T::StaticClass(), or T::StaticStruct())
 20		 * AND
 21		 * 2) InPropertyType is derived from FProperty
 22		 */
 23		template<typename InStructType, typename InPropertyType>
 24		constexpr bool TFindPropertyCondition_V =
 25			(
 26				TModels_V<CStaticClassProvider, InStructType> ||
 27				TModels_V<CStaticStructProvider, InStructType>
 28			) &&
 29			std::is_base_of_v<FProperty, InPropertyType>;
 30	
 31		template<typename InStructType, typename InPropertyType = FProperty
 32			UE_REQUIRES(TFindPropertyCondition_V<InStructType, InPropertyType>)>
 33		InPropertyType* GetProperty(FName InMemberPropertyName)
 34		{
 35			if constexpr (TModels_V<CStaticClassProvider, InStructType>)
 36			{
 37				return FindFProperty<InPropertyType>(InStructType::StaticClass(), InMemberPropertyName);
 38			}
 39	
 40			else if constexpr (TModels_V<CStaticStructProvider, InStructType>)
 41			{
 42				return FindFProperty<InPropertyType>(InStructType::StaticStruct(), InMemberPropertyName);
 43			}
 44	
 45			else
 46			{
 47				checkNoEntry();
 48				return nullptr;
 49			}
 50		}
 51	}
