 562	
 563	const FProperty* PCGPropertyHelpers::FindPropertyInUserDefinedStruct(const UUserDefinedStruct* InStruct, const FName InName)
 564	{
 565		if (!InStruct)
 566		{
 567			return nullptr;
 568		}
 569	
 570		TArray<FName, TInlineAllocator<2>> NamesToLookFor = { InName };
 571	
 572		if (!InName.IsValidXName())
 573		{
 574			NamesToLookFor.Add(MakeObjectNameFromDisplayLabel(InName.ToString(), NAME_None));
 575		}
 576	
 577		for (TFieldIterator<const FProperty> PropIt(InStruct, EFieldIterationFlags::IncludeSuper); PropIt; ++PropIt)
 578		{
 579			const FString PropertyNameStr = InStruct->GetAuthoredNameForField(*PropIt);
 580			const FName PropertyName = *PropertyNameStr;
 581			if (NamesToLookFor.Contains(PropertyName)
 582				|| (!PropertyName.IsValidXName() && NamesToLookFor.Contains(MakeObjectNameFromDisplayLabel(PropertyNameStr, NAME_None))))
 583			{
 584				return *PropIt;
 585			}
 586		}
