 11	/**
 12	 * FFieldIterator_BlueprintVisible
 13	 */
 14	TArray<FFieldVariant> FFieldIterator_BlueprintVisible::GetFields(const UStruct* Struct, const FName FieldName, const UStruct* ContainerStruct) const
 15	{
 16		TArray<FFieldVariant> Result;
 17		for (TFieldIterator<FProperty> PropertyIt(Struct, EFieldIteratorFlags::IncludeSuper); PropertyIt; ++PropertyIt)
 18		{
 19			FProperty* Property = *PropertyIt;
 20			if (Property->HasAnyPropertyFlags(CPF_BlueprintVisible | CPF_BlueprintAssignable | CPF_Parm)
 21				&& !Property->HasAnyPropertyFlags(CPF_Deprecated | CPF_EditorOnly))
 22			{
 23				Result.Add(FFieldVariant(Property));
 24			}
 25		}
 26		for (TFieldIterator<UFunction> FunctionItt(Struct, EFieldIteratorFlags::IncludeSuper); FunctionItt; ++FunctionItt)
 27		{
 28			UFunction* Function = *FunctionItt;
 29			if (Function->HasAnyFunctionFlags(FUNC_BlueprintCallable))
 30			{
 31				Result.Add(FFieldVariant(Function));
 32			}
 33		}
 34		return Result;
 35	}
