 47	void AAuraEffectActor::OnOverlap(AActor* TargetActor)
 48	{
 49		if (InstantEffectApplicationPolicy == EEffectApplicationPolicy::ApplyOnOverlap)
 50		{
 51			ApplyEffectToTarget(TargetActor, InstantGameplayEffectClass);
 52		}
 53		if (DurationEffectApplicationPolicy == EEffectApplicationPolicy::ApplyOnOverlap)
 54		{
 55			ApplyEffectToTarget(TargetActor, DurationGameplayEffectClass);
 56		}
 57		if (InfiniteEffectApplicationPolicy == EEffectApplicationPolicy::ApplyOnOverlap)
 58		{
 59			ApplyEffectToTarget(TargetActor, InfiniteGameplayEffectClass);
 60		}
 61	}
 62	
 63	void AAuraEffectActor::OnEndOverlap(AActor* TargetActor)
 64	{
 65		if (InstantEffectApplicationPolicy == EEffectApplicationPolicy::ApplyOnEndOverlap)
 66		{
 67			ApplyEffectToTarget(TargetActor, InstantGameplayEffectClass);
 68		}
 69		if (DurationEffectApplicationPolicy == EEffectApplicationPolicy::ApplyOnEndOverlap)
 70		{
 71			ApplyEffectToTarget(TargetActor, DurationGameplayEffectClass);
 72		}
 73		if (InfiniteEffectApplicationPolicy == EEffectApplicationPolicy::ApplyOnEndOverlap)
 74		{
 75			ApplyEffectToTarget(TargetActor, DurationGameplayEffectClass);
 76		}
 77		// 判断移除永久持续效果
 78		if (InfiniteEffectRemovePolicy == EEffectRemovePolicy::RemoveOnEndOverlap)
 79		{
